//React
import React, { useState } from 'react';

//CSS
import './Header.css';
import './accountDropDown/AccountDropDown.css';

//Assets
import logo from '../../assets/logo.png';

//Components
import AccountDropDown from './accountDropDown/AccountDropDown';

const Header = () => {
    const [sidebar, setSidebar] = useState(true);

    const showSidebar = () => setSidebar(!sidebar);


    return (
        <div className='header_container'>
            <div className='accountdropdown_container'>
            <div className='header'>
                <div className='header_left'>
                    Shocker Pools
                </div>
                <div className='header_middle'>
                    <div className='header_right_left'>
                        <ul>
                            <li><a>Pools</a></li>
                            <li><a>Hot Tubs</a></li>
                            <li><a>Chemicals</a></li>
                            <li><a>Furniture</a></li>
                            <li><a>Grills</a></li>
                            <li><a>Services</a></li>
                        </ul>
                    </div>
                </div>
                <div className='header_right'>
                    <div className='header_right_right'>
                        <ul>
                            <li onClick={showSidebar}><a>Account</a></li>
                        </ul>
                        
                    </div>
                </div>
            </div>
            <AccountDropDown className={sidebar ? 'dropdown_container-active' : 'dropdown_container'} />
            </div>
        </div>
    );
}

export default Header;
